mapper.update:
create table ${siteId}_relate_keyword (
	id int not null auto_increment,
	keyword varchar(64),
	updateTime timestamp,
	primary key (id)
);

mapper.select:
select a.id, a.keyword, concat(group_concat(b.value)) value, a.updateTime from ${siteId}_relate_keyword a left join ${siteId}_relate_keyword_value b on b.keyId = a.id
${whereCondition}
group by a.id limit #{start}, #{end}
