mapper.update:
create table ${siteId}_relate_keyword (
	id int not null auto_increment,
	keyword varchar(64),
	updateTime timestamp,
	primary key (id)
) ENGINE=InnoDB, CHARACTER SET=utf8 COLLATE utf8_unicode_ci;

mapper.select:
select a.id, a.keyword, concat(group_concat(b.value)) value, a.updateTime from ${siteId}_relate_keyword a left join ${siteId}_relate_keyword_value b on b.keyId = a.id
where ${whereCondition}
group by a.id limit #{start}, #{end}

select count(*) from ${siteId}_relate_keyword a left join 
${siteId}_relate_keyword_value b on b.keyId = a.id

where ${whereCondition}

mapper.update:
update ${siteId}_relate_keyword set 
keyword = #{entry.keyword},
updateTime = #{entry.updateTime}
where id = #{entry.id}
