mapper.update:
create table ${siteId}_hit (
	categoryId varchar(30), 
	timeId varchar(11),
	hit int,
	primary key (timeId)
) ENGINE=InnoDB;

mapper.select:
select * from ${siteId}_hit 
where
categoryId=#{categoryId} 
and timeId=#{timeId}

select * from ${siteId}_hit 
where 
categoryId=#{categoryId} 
and timeId=(select min(timeId) 
from ${siteId}_hit where timeId like '${dFilter}%') 
limit 1

select * from ${siteId}_hit  

	categoryId=#{categoryId}

		and timeId between #{from} and #{to}

	and timeId like '${dFilter}%'

select count(*) from ${siteId}_hit 

	categoryId=#{categoryId}

		and timeId between #{from} and #{to}

	and timeId like '${dFilter}%'

select sum(hit) from ${siteId}_hit 
where 
categoryId=#{categoryId}
and timeId between #{from} and #{to}

mapper.update:
update ${siteId}_hit set hit = #{hit} 
where 
categoryId=#{categoryId} 
and timeId = #{timeId}

mapper.delete:
delete ${siteId}_hit set hit = #{hit} 
where 
categoryId=#{categoryId}
and timeId = #{timeId}

delete ${siteId}_hit set hit = #{hit} 
where 
categoryId=#{categoryId}
and timeId between #{from} and #{to}
