mapper.select:
select * from ${site}_${category}_hit


		and keyword = #{keyword}


		and timeId between #{from} and #{to}

	and timeId like '${dType}%'

select count(*) from ${site}_${category}_hit 


		and keyword = #{keyword}


		and timeId between #{from} and #{to}

	and timeId like '${dType}%'

select sum(hit) from ${site}_${category}_hit 


	and keyword = #{keyword} 

	and timeId between #{from} and #{to}

mapper.delete:
delete ${site}_${category}_hit set hit = #{hit}


		and keyword=#{keyword}

	and timeId between #{from} and #{to}
