mapper.select:
select * from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId=#{timeId}
and typeId=#{typeId}
and dtype=#{dtype}

select * from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId=#{timeId}

mapper.update:
delete from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId = #{timeId}
and typeId = #{typeId}
