mapper.select:
select timeId, dtype, hit
from ${siteId}_type_hit_${typeId}
limit 1

select * from ${siteId}_type_hit_${typeId} where 
timeId=(
	select min(timeId) from ${siteId}_type_hit_${typeId} where timeId like '${dFilter}%'

	and dtype=#{dtype}

) 
limit 1

select * from ${siteId}_type_hit_${typeId} where 
timeId=(
	select max(timeId) from ${siteId}_type_hit_${typeId} where timeId like '${dFilter}%'

	and dtype=#{dtype}

) 
limit 1

select a.* from (
select * from ${siteId}_type_hit_${typeId}


		and dtype = #{dtype}


		and timeId between #{from} and #{to}

	and timeId like '${dFilter}%'


group by dtype

) a

select count(*) from ${siteId}_type_hit_${typeId} 


		and dtype = #{dtype}


		and timeId between #{from} and #{to}

	and timeId like '${dFilter}%'

select sum(hit) from ${siteId}_type_hit_${typeId} 


	and dtype = #{dtype} 

	and timeId between #{from} and #{to}

mapper.delete:
delete ${siteId}_type_hit_${typeId} set hit = #{hit}


		and dtype=#{dtype}

	and timeId between #{from} and #{to}
