mapper.select:
select * from (
	select keyword, sum(hit) as count from ${siteId}_click_hit_keyword 
	where timeId=#{timeId}
	group by keyword
	order by count desc
) as A limit ${topCount}

select sum(hit) from ${siteId}_click_hit_keyword 

	timeId=#{timeId}
	and keyword = #{keyword}

select hit from ${siteId}_click_hit_keyword 

	timeId=#{timeId}
	and keyword = #{keyword}
	and clickType = #{clickType}
