mapper.select:
select categoryId, timeId, rank, keyword, count, countDiff, rankDiffType, rankDiff from (
select row_number() over() as r, a.*
from ${siteId}_empty_keyword a
where categoryId = #{categoryId}
and timeId = #{timeId}
order by rank asc
) a

where r >= #{start} and r < ${start} + ${length}

select count(*)
from ${siteId}_empty_keyword
where categoryId = #{categoryId}
and timeId = #{timeId}

mapper.update:
update ${siteId}_empty_keyword 
set 
rank = #{vo.rank},
keyword = #{vo.keyword},
count = #{vo.count},
countDiff = #{vo.countDiff},
rankDiffType = #{vo.rankDiffType},
rankDiff = #{vo.rankDiff}
where categoryId = #{vo.categoryId}
and timeId = #{vo.timeId}

delete
from ${siteId}_empty_keyword
where categoryId = #{categoryId}
and timeId = #{timeId}
