mapper.select:
select * from ${siteId}_hit_keyword 
where 
categoryId=#{categoryId} 
and timeId=#{timeId}
and keyword=#{keyword}

select count(*) from ${siteId}_hit_keyword 
where 
categoryId=#{categoryId} 
and timeId=#{timeId}

mapper.delete:
delete from ${siteId}_hit_keyword
where 
categoryId=#{categoryId}
and timeId = #{timeId}

mapper.select:
select * from ${siteId}_hit_keyword

	categoryId=#{categoryId}

	and keyword = #{keyword}


		and timeId >= #{from} 


		and timeId <= #{to}

select count(*) from ${siteId}_hit_keyword 

	categoryId=#{categoryId}

		and keyword = #{keyword}


		and timeId >= #{from} 


		and timeId <= #{to}

	and timeId like '${dFilter}%'

select * from ${siteId}_hit_keyword 
where
categoryId=#{categoryId} 
and timeId=(
	select min(timeId) from ${siteId}_hit_keyword 
	where timeId like '${dFilter}%'

	and keyword=#{keyword}

) 
limit 1

select * from ${siteId}_hit_keyword where 
timeId=(
	select max(timeId) from ${siteId}_hit_keyword where timeId like '${dFilter}%'

	and keyword=#{keyword}

) 
limit 1

select sum(hit) from ${siteId}_hit_keyword 

	categoryId=#{categoryId}

	and keyword = #{keyword} 

	and timeId between #{from} and #{to}

mapper.update:
update ${siteId}_hit_keyword set hit = #{hit} 
where 
categoryId=#{categoryId} 
and timeId = #{timeId} 
and keyword = #{keyword}

mapper.delete:
delete from 
${siteId}_hit_keyword
where 
categoryId=#{categoryId}
and timeId = #{timeId}
and keyword = #{keyword}

delete from 
${siteId}_hit_keyword set hit = #{hit}

	categoryId=#{categoryId}

		and keyword=#{keyword}

	and timeId between #{from} and #{to}
