mapper.select:
select * from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId=#{timeId}
and typeId=#{typeId}
and dtype=#{dtype}

select count(*) from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId=#{timeId}
and typeId=#{typeId}

select * from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId=#{timeId}
and typeId=#{typeId}

select * from ${siteId}_type_hit 

	categoryId=#{categoryId}

		and timeId >= #{from} 


		and timeId <= #{to}

	and typeId=#{typeId}

order by timeId asc

select categoryId, timeId, typeId, dtype, sum(hit) as hit
from ${siteId}_type_hit 

	categoryId=#{categoryId}

		and timeId >= #{timeFrom} 


		and timeId <= #{timeTo}


group by typeId, dtype

select dtype, sum(hit) as hit from ${siteId}_type_hit 

	categoryId=#{categoryId}

		and timeId >= #{from} 


		and timeId <= #{to}

	and typeId=#{typeId}

group by dtype
order by hit desc

mapper.update:
delete from ${siteId}_type_hit 
where categoryId = #{categoryId}
and timeId = #{timeId}
and typeId = #{typeId}
