mapper.insert:
insert into UserAccount ( 
name, userId, password, email, sms, userLevel
)
values (
#{name}, #{userId}, #{password}, #{email}, #{sms}, #{userLevel}
)

mapper.update:
update UserAccount 
set name=#{name}, password=#{password}, email=#{email}, sms=#{sms}, userLevel=#{userLevel}
where id=#{id}
